package Ifelse;

public class Tshirt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Smith wants to buy a tshirt, if the size is only 's' and price<1000
	char size='s';
	int price = 800;
	
	if(size=='s' && price<=1000)
	{
		System.out.println("Tom can buy this tshirt");
	}
	
		System.out.println("Don't buy this tshirt");
	
	}

}
