package Operators;

public class Example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int num1=7;
	//int num2=5;
	
	//int result=num1 %  num2;
	
	//num1 = num1+2;
	
	num1 +=2;
	
	num1 -=2;
	
	num1 *=8;
	
	num1++;
	//fetch the value and post that it will increment
	
	num1--;
	
	++num1;
	
	--num1;
	//first decrement and later fetch the value
	
	
	int result = ++num1;
	
	System.out.println(result);
	
	
	
	
	
	
		
	}

}
